
public class IfStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// performs blocks of code in the case that the condition is true;
		
		// equal is ==
		// this: = is addressing value 
		
		int age = 18;
		
		if(age>=75)
		{
			System.out.println("Voce e um veio");
		}
		else if(age >= 18)
		{
			System.out.println("Voce e um adulto");
		}
		else if(age>=13)
		{
			System.out.println("Voce e um aborrecente");
		}
		else
		{
			System.out.println("Voce e um adulto");
		}
		
	}

}
